mapper.select:
SELECT *
FROM board_count
WHERE board_id = #{board_id}
AND id = #{id}


SELECT id, board_id, title, content 
FROM board
WHERE board_id = #{board_id}

mapper.update:

update board
SET is_delete = '1'
WHERE board_id = #{board_id}


update board_comment
SET is_delete = '1'
WHERE comment_id = #{comment_id}


update board
SET title = #{title},
	content = #{content}
WHERE board_id = #{board_id}


UPDATE board_file
SET file_name = #{file_name}
WHERE board_id = #{board_id}


update board_comment
SET content = #{content}
WHERE comment_id = #{comment_id}
